import { supabase } from '../supabaseClient';

/**
 * Industry-Standard Company Creation Service
 * Uses secure Edge Functions for all admin operations
 */
export class CompanyService {

  /**
   * Complete Company Creation Workflow
   * Calls secure Edge Function to handle all steps with service key
   */
  static async createCompanyWithOwner(formData) {
    const { companyName, ownerFirstName, ownerLastName, ownerEmail, ownerPhone, tempPassword } = formData;

    try {
      console.log('üöÄ Starting company creation via Edge Function...');
      console.log('üìù Form data:', { companyName, ownerFirstName, ownerLastName, ownerEmail, ownerPhone });

      // Call secure Edge Function that handles all steps with service key
      const { data, error } = await supabase.functions.invoke('create-company-with-owner', {
        body: {
          companyName,
          ownerFirstName,
          ownerLastName,
          ownerEmail,
          ownerPhone,
          tempPassword
        }
      });

      if (error) {
        console.error('‚ùå Edge Function call failed:', error);
        throw new Error(`Failed to create company: ${error.message}`);
      }

      if (!data.success) {
        console.error('‚ùå Company creation failed:', data.error);
        throw new Error(data.error || 'Company creation failed');
      }

      console.log('üéâ Company creation completed successfully!');
      console.log('‚úÖ Result:', data);

      return {
        company: data.company,
        authUser: data.authUser,
        profile: data.profile,
        success: true
      };

    } catch (error) {
      console.error('‚ùå Company creation workflow failed:', error);
      throw error;
    }
  }

  /**
   * Get all companies with owner information via Edge Function
   */
  static async getAllCompanies() {
    try {
      console.log('üè¢ Fetching companies via Edge Function...');

      // Call secure Edge Function that handles profile fetching with service key
      const { data, error } = await supabase.functions.invoke('get-companies');

      if (error) {
        console.error('‚ùå Edge Function call failed:', error);
        throw new Error(`Failed to fetch companies: ${error.message}`);
      }

      if (!data.success) {
        console.error('‚ùå Companies fetch failed:', data.error);
        throw new Error(data.error || 'Companies fetch failed');
      }

      console.log('‚úÖ Companies fetched successfully:', data.companies.length);
      return data.companies;

    } catch (error) {
      console.error('‚ùå Failed to fetch companies:', error);
      throw error;
    }
  }

  /**
   * Get company details with owner and employees
   */
  static async getCompanyDetails(companyId) {
    try {
      const { data: company, error } = await supabase
        .from('companies')
        .select(`
          id,
          name,
          created_at
        `)
        .eq('id', companyId)
        .single();

      if (error) {
        console.error('‚ùå Error fetching company details:', error);
        throw error;
      }

      // Get all profiles for this company
      const { data: profiles, error: profilesError } = await supabase
        .from('profiles')
        .select('id, full_name, role, phone, created_at')
        .eq('company_id', companyId);

      if (profilesError) {
        console.error('‚ùå Error fetching profiles:', profilesError);
        throw profilesError;
      }

      // Find owner and employees
      const owner = profiles?.find(p => p.role === 'OWNER');
      const employees = profiles?.filter(p => p.role !== 'OWNER') || [];

      return {
        ...company,
        owner_profile: owner,
        owner_email: 'Available via Edge Function',
        employees: employees.map(emp => ({ ...emp, email: 'Available via Edge Function' }))
      };
    } catch (error) {
      console.error('‚ùå Failed to fetch company details:', error);
      throw error;
    }
  }
}
