import { supabaseAnon, supabaseService } from '../supabaseClient';

/**
 * Industry-Standard Company Creation Service
 * Follows exact 3-step workflow as specified
 */
export class CompanyService {

  /**
   * Step A: Create a New Company
   * Insert company record with basic info (using anon client)
   */
  static async createCompany(companyName) {
    try {
      console.log('🏢 Step A: Creating company:', companyName);

      const { data: company, error } = await supabaseAnon
        .from('companies')
        .insert({ name: companyName })
        .select()
        .single();

      if (error) {
        console.error('❌ Error creating company:', error);
        throw new Error(`Failed to create company: ${error.message}`);
      }

      console.log('✅ Company created:', company);
      return company;
    } catch (error) {
      console.error('❌ Company creation failed:', error);
      throw error;
    }
  }

  /**
   * Step B: Create the Owner Auth Account
   * Use Supabase Admin API to create authenticated user
   */
  static async createOwnerAuthAccount(ownerEmail, tempPassword) {
    try {
      console.log('👤 Step B: Creating owner auth account for:', ownerEmail);

      const { data: authUser, error: authError } = await supabaseService.auth.admin.createUser({
        email: ownerEmail,
        password: tempPassword,
        email_confirm: true,
      });

      if (authError) {
        console.error('❌ Error creating auth user:', authError);
        throw new Error(`Failed to create auth account: ${authError.message}`);
      }

      if (!authUser?.user?.id) {
        throw new Error('Auth user creation returned no user ID');
      }

      console.log('✅ Auth user created:', authUser.user.email, 'ID:', authUser.user.id);
      return authUser;
    } catch (error) {
      console.error('❌ Auth account creation failed:', error);
      throw error;
    }
  }

  /**
   * Step C: Create the Owner Profile
   * Insert profile record using SERVICE KEY (critical!)
   */
  static async createOwnerProfile(authUserId, companyId, ownerFirstName, ownerLastName, ownerPhone) {
    try {
      console.log('📋 Step C: Creating owner profile for user:', authUserId);

      const fullName = `${ownerFirstName} ${ownerLastName}`;

      // Insert profile record - MUST use service key for profiles table
      const { data: profile, error: profileError } = await supabaseService
        .from('profiles')
        .insert({
          id: authUserId,
          company_id: companyId,
          role: 'OWNER',
          full_name: fullName,
          phone: ownerPhone || null,
        })
        .select()
        .single();

      if (profileError) {
        console.error('❌ Error creating profile:', profileError);
        throw new Error(`Failed to create profile: ${profileError.message}`);
      }

      console.log('✅ Profile created:', profile);
      return profile;
    } catch (error) {
      console.error('❌ Owner profile creation failed:', error);
      throw error;
    }
  }

  /**
   * Complete Company Creation Workflow
   * Executes all 3 steps with proper error handling and rollback
   */
  static async createCompanyWithOwner(formData) {
    const { companyName, ownerFirstName, ownerLastName, ownerEmail, ownerPhone, tempPassword } = formData;

    let createdCompany = null;
    let createdAuthUser = null;

    try {
      console.log('🚀 Starting complete company creation workflow...');

      // Step A: Create Company
      createdCompany = await this.createCompany(companyName);

      // Step B: Create Owner Auth Account
      createdAuthUser = await this.createOwnerAuthAccount(ownerEmail, tempPassword);

      // Step C: Create Owner Profile
      const createdProfile = await this.createOwnerProfile(
        createdAuthUser.user.id,
        createdCompany.id,
        ownerFirstName,
        ownerLastName,
        ownerPhone
      );

      console.log('🎉 Company creation workflow completed successfully!');

      return {
        company: createdCompany,
        authUser: createdAuthUser,
        profile: createdProfile,
        success: true
      };

    } catch (error) {
      console.error('❌ Company creation workflow failed, attempting rollback...');

      // Rollback: Delete created records
      try {
        if (createdAuthUser?.user?.id) {
          console.log('🔄 Rolling back auth user...');
          await supabaseService.auth.admin.deleteUser(createdAuthUser.user.id);
        }

        if (createdCompany?.id) {
          console.log('🔄 Rolling back company...');
          await supabaseService.from('companies').delete().eq('id', createdCompany.id);
        }

        console.log('✅ Rollback completed');
      } catch (rollbackError) {
        console.error('❌ Rollback failed:', rollbackError);
      }

      throw error;
    }
  }

  /**
   * Get all companies with owner information and email from auth.users
   */
  static async getAllCompanies() {
    try {
      // Get companies and find owners via profiles table
      const { data: companies, error } = await supabaseService
        .from('companies')
        .select(`
          id,
          name,
          created_at
        `)
        .order('created_at', { ascending: false });

      if (error) {
        console.error('❌ Error fetching companies:', error);
        throw error;
      }

      // For each company, find the owner (profile with role = 'OWNER')
      const companiesWithOwners = await Promise.all(
        (companies || []).map(async (company) => {
          try {
            // Find owner profile for this company
            const { data: ownerProfile, error: profileError } = await supabaseService
              .from('profiles')
              .select('id, full_name, phone')
              .eq('company_id', company.id)
              .eq('role', 'OWNER')
              .single();

            if (profileError && profileError.code !== 'PGRST116') {
              console.error('Error fetching owner profile:', profileError);
            }

            // Get owner email from auth.users if we have an owner
            let ownerEmail = 'N/A';
            if (ownerProfile?.id) {
              try {
                const { data: authUser } = await supabaseService.auth.admin.getUserById(ownerProfile.id);
                ownerEmail = authUser?.user?.email || 'N/A';
              } catch (error) {
                console.error('Error fetching owner email:', error);
              }
            }

            return {
              ...company,
              owner_profile: ownerProfile,
              owner_email: ownerEmail
            };
          } catch (error) {
            console.error('Error processing company:', error);
            return {
              ...company,
              owner_profile: null,
              owner_email: 'N/A'
            };
          }
        })
      );

      return companiesWithOwners;
    } catch (error) {
      console.error('❌ Failed to fetch companies:', error);
      throw error;
    }
  }

  /**
   * Get company details with owner and employees, including emails from auth.users
   */
  static async getCompanyDetails(companyId) {
    try {
      const { data: company, error } = await supabaseService
        .from('companies')
        .select(`
          id,
          name,
          created_at
        `)
        .eq('id', companyId)
        .single();

      if (error) {
        console.error('❌ Error fetching company details:', error);
        throw error;
      }

      // Get all profiles for this company
      const { data: profiles, error: profilesError } = await supabaseService
        .from('profiles')
        .select('id, full_name, role, phone, created_at')
        .eq('company_id', companyId);

      if (profilesError) {
        console.error('❌ Error fetching profiles:', profilesError);
        throw profilesError;
      }

      // Find owner and employees
      const owner = profiles?.find(p => p.role === 'OWNER');
      const employees = profiles?.filter(p => p.role !== 'OWNER') || [];

      // Get owner email
      let ownerEmail = 'N/A';
      if (owner?.id) {
        try {
          const { data: authUser } = await supabaseService.auth.admin.getUserById(owner.id);
          ownerEmail = authUser?.user?.email || 'N/A';
        } catch (error) {
          console.error('Error fetching owner email:', error);
        }
      }

      // Get employee emails
      const employeesWithEmails = await Promise.all(
        employees.map(async (employee) => {
          try {
            const { data: authUser } = await supabaseService.auth.admin.getUserById(employee.id);
            return {
              ...employee,
              email: authUser?.user?.email || 'N/A'
            };
          } catch (error) {
            console.error('Error fetching employee email:', error);
            return {
              ...employee,
              email: 'N/A'
            };
          }
        })
      );

      return {
        ...company,
        owner_profile: owner,
        owner_email: ownerEmail,
        employees: employeesWithEmails
      };
    } catch (error) {
      console.error('❌ Failed to fetch company details:', error);
      throw error;
    }
  }
}
