import React, { createContext, useContext, useState, useEffect } from 'react';
import { supabase } from '../supabaseClient';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    let mounted = true;

    const checkAuth = async () => {
      if (!mounted) return;

      try {
        console.log('üîç Admin Dashboard - Checking authentication...');

        const { data: { session }, error } = await supabase.auth.getSession();

        if (!mounted) return;

        if (error) {
          console.error('‚ùå Admin Dashboard - Auth error:', error);
          setUser(null);
          setLoading(false);
          return;
        }

        if (!session || !session.user) {
          console.log('‚ùå Admin Dashboard - No session found');
          setUser(null);
          setLoading(false);
          return;
        }

        // Get user profile
        const { data: profile, error: profileError } = await supabase
          .from('profiles')
          .select('id, first_name, last_name, role, company_id')
          .eq('id', session.user.id)
          .single();

        if (!mounted) return;

        if (profileError || !profile) {
          console.error('‚ùå Admin Dashboard - Profile error:', profileError);
          setUser(null);
          setLoading(false);
          return;
        }

        // Only allow APP_OWNER role for admin dashboard
        if (profile.role !== 'APP_OWNER') {
          console.log('‚ùå Admin Dashboard - Insufficient permissions. Role:', profile.role);
          setUser(null);
          setLoading(false);
          return;
        }

        const userData = {
          id: profile.id,
          email: session.user.email,
          full_name: `${profile.first_name || ''} ${profile.last_name || ''}`.trim(),
          role: profile.role,
          company_id: profile.company_id
        };

        console.log('‚úÖ Admin Dashboard - Authentication successful:', userData.email);
        setUser(userData);

      } catch (error) {
        console.error('‚ùå Admin Dashboard - Auth check failed:', error);
        if (mounted) {
          setUser(null);
        }
      }

      if (mounted) {
        setLoading(false);
      }
    };

    // Initial check
    checkAuth();

    // Listen for auth changes
    const { data: { subscription } } = supabase.auth.onAuthStateChange(async (event, session) => {
      if (!mounted) return;

      console.log('üîê Admin Dashboard - Auth state changed:', event);

      if (event === 'SIGNED_OUT' || !session) {
        setUser(null);
        setLoading(false);
      } else if (event === 'SIGNED_IN' || event === 'TOKEN_REFRESHED') {
        // Small delay to prevent race conditions
        setTimeout(() => {
          if (mounted) checkAuth();
        }, 100);
      }
    });

    return () => {
      mounted = false;
      subscription.unsubscribe();
    };
  }, []);

  const login = async (email, password) => {
    try {
      console.log('üîê Admin Dashboard - Attempting login:', email);

      const { data, error } = await supabase.auth.signInWithPassword({
        email,
        password
      });

      if (error) {
        throw new Error(error.message);
      }

      return data;
    } catch (error) {
      console.error('‚ùå Admin Dashboard - Login failed:', error);
      throw error;
    }
  };

  const logout = async () => {
    try {
      await supabase.auth.signOut();
      setUser(null);
    } catch (error) {
      console.error('‚ùå Admin Dashboard - Logout failed:', error);
    }
  };

  const value = {
    user,
    loading,
    login,
    logout
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
