import { createClient } from '@supabase/supabase-js';

// Environment variables
const supabaseUrl = process.env.REACT_APP_SUPABASE_URL || 'https://amgtktrwpdsigcomavlg.supabase.co';
const supabaseAnonKey = process.env.REACT_APP_SUPABASE_ANON_KEY;
const supabaseServiceKey = process.env.REACT_APP_SUPABASE_SERVICE_KEY;

if (!supabaseAnonKey) {
  console.error('‚ùå REACT_APP_SUPABASE_ANON_KEY is required');
}

if (!supabaseServiceKey) {
  console.error('‚ùå REACT_APP_SUPABASE_SERVICE_KEY is required for admin operations');
}

// Standard client for authentication and regular operations
export const supabase = createClient(supabaseUrl, supabaseAnonKey, {
  auth: {
    persistSession: true,
    autoRefreshToken: true,
    detectSessionInUrl: true,
    storageKey: 'tm-admin-auth',
  }
});

// Admin client with service key for admin operations (user creation, etc.)
export const supabaseAdmin = createClient(supabaseUrl, supabaseServiceKey, {
  auth: {
    autoRefreshToken: false,
    persistSession: false
  }
});

console.log('üîß Admin Dashboard Supabase clients initialized');
console.log('üìç Supabase URL:', supabaseUrl);
console.log('üîë Anon Key:', supabaseAnonKey ? 'Present' : 'Missing');
console.log('üîë Service Key:', supabaseServiceKey ? 'Present' : 'Missing');
