CREATE OR REPLACE FUNCTION public.calculate_work_order_totals_with_tax(p_work_order_id uuid)
 RETURNS jsonb
 LANGUAGE plpgsql
AS $function$
DECLARE
    subtotal NUMERIC := 0;
    tax_amount NUMERIC := 0;
    total_amount NUMERIC := 0;
    company_tax_rate NUMERIC := 0;
    result JSONB;
BEGIN
    -- Get company default tax rate
    SELECT cs.default_tax_rate INTO company_tax_rate
    FROM work_orders wo
    JOIN companies c ON wo.company_id = c.id
    JOIN company_settings cs ON c.id = cs.company_id
    WHERE wo.id = p_work_order_id;

    -- Calculate subtotal from line items
    SELECT COALESCE(SUM(total_price), 0) INTO subtotal
    FROM work_order_line_items
    WHERE work_order_id = p_work_order_id;

    -- Calculate tax (using line item tax rates or company default)
    SELECT COALESCE(SUM(total_price * COALESCE(tax_rate, company_tax_rate)), 0) INTO tax_amount
    FROM work_order_line_items
    WHERE work_order_id = p_work_order_id;

    total_amount := subtotal + tax_amount;

    -- Update work order
    UPDATE work_orders
    SET subtotal = subtotal,
        tax_amount = tax_amount,
        total_amount = total_amount,
        updated_at = NOW()
    WHERE id = p_work_order_id;

    result := jsonb_build_object(
        'work_order_id', p_work_order_id,
        'subtotal', subtotal,
        'tax_amount', tax_amount,
        'total_amount', total_amount
    );

    RETURN result;
END;
$function$

CREATE OR REPLACE FUNCTION public.enforce_work_order_status()
 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
BEGIN
    -- Validate status transition
    IF OLD.status IS NOT NULL AND NOT validate_work_order_status_transition(OLD.status, NEW.status) THEN
        RAISE EXCEPTION 'Invalid status transition from % to %', OLD.status, NEW.status;
    END IF;

    -- Auto-populate timestamps
    IF NEW.status = 'in_progress' AND NEW.actual_start IS NULL THEN
        NEW.actual_start := NOW();
    END IF;

    IF NEW.status = 'completed' AND NEW.actual_end IS NULL THEN
        NEW.actual_end := NOW();
    END IF;

    -- Auto-generate reference numbers
    IF NEW.work_order_number IS NULL OR NEW.work_order_number = '' THEN
        NEW.work_order_number := generate_work_order_number(NEW.company_id);
    END IF;

    RETURN NEW;
END;
$function$

